package tech.damko.videoteka.presentation;

import java.awt.Graphics2D;
import java.awt.RenderingHints;
import java.awt.image.BufferedImage;

import javax.swing.ImageIcon;
import javax.swing.JOptionPane;

public class Pictures {

    private String searchMoviePic;
    private String notAvailablePic;

    public Pictures(){
        this.searchMoviePic = "";
        this.notAvailablePic = "";

    }

    public String getSearchMoviePic(){
        return this.searchMoviePic;
    }

    public String getNotAvailablePic(){
        return this.notAvailablePic;
    }

    public BufferedImage resizeImage(final int WIDTH,final int HEIGHT,byte[] im){

        BufferedImage bi = null;
        try {
            ImageIcon ii = new ImageIcon(im);
            bi = new BufferedImage(WIDTH, HEIGHT, BufferedImage.TYPE_INT_RGB);
            Graphics2D g2d = bi.createGraphics();
            g2d.addRenderingHints(new RenderingHints(RenderingHints.KEY_RENDERING,RenderingHints.VALUE_RENDER_QUALITY));
            g2d.drawImage(ii.getImage(), 0, 0, WIDTH, HEIGHT, null);
        } catch (Exception e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(null, "Error: Pictures.resizeImage.");
        }
        return bi;
    }

}
